// components/AskOut.js
import React,{ useState } from 'react';
// import pandaImage from '../assets/panda.svg'; // Ensure you have a similar SVG graphic

function AskOut() {
    const [position, setPosition] = useState({ top: 0, left: 0 });

    // Function to handle click on the "No" button and move it randomly
    const handleNoClick = () => {
        // Generate random positions within the viewport
        const newPosition = {
        top: Math.random() * (window.innerHeight - 50), // Avoiding edge overflow
        left: Math.random() * (window.innerWidth - 50)  // Avoiding edge overflow
        };
        setPosition(newPosition);
    };


    return (
        <div style={styles.container}>
            {/* <img src={pandaImage} alt="Cute Panda" style={styles.image}/> */}
            <p style={styles.text}>Do you wanna go out with me?<br/>Are you free tomorrow?</p>
            <button style={styles.button}>Yes</button>
            <button style={styles.button}>No</button>
        </div>
    );
}

export default AskOut;

// Define styles in your component for simplicity
const styles = {
    container: {
        textAlign: 'center',
        backgroundColor: '#b8e994',
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center'
    },
    image: {
        width: '150px',
        marginBottom: '20px'
    },
    text: {
        color: '#4a4a4a',
        fontFamily: '"Comic Sans MS", cursive, sans-serif',
        fontSize: '24px',
        marginBottom: '20px'
    },
    button: {
        fontSize: '20px',
        margin: '10px',
        padding: '10px 20px',
        borderRadius: '20px',
        border: 'none',
        backgroundColor: '#f9f9f9',
        cursor: 'pointer',
        outline: 'none'
    }
};
