import React, { useState } from 'react';

function AskOut() {
    const [position, setPosition] = useState({ top: 0, left: 0 });
    const [clickCount, setClickCount] = useState(0);  // Add a counter for clicks

    // Function to handle click on the "No" button and move it randomly
    const handleNoClick = () => {
        const newPosition = {
            top: Math.random() * (window.innerHeight - 100),
            left: Math.random() * (window.innerWidth - 100)
        };
        setPosition(newPosition);
        setClickCount(prevCount => prevCount + 1);  // Increment the counter on each click
    };

    return (
        <div style={styles.container}>
            <p style={styles.text}>Do you wanna go out with me?<br/>Are you free tomorrow?</p>
            <button style={styles.yesButton} onClick={() => alert('Yes! :)')}>Yes</button>
            <button 
                key={clickCount}  // Use the click count as the key
                style={{
                    ...styles.noButton,
                    top: `${position.top}px`,
                    left: `${position.left}px`
                }}
                onClick={handleNoClick}
            >
                No
            </button>
        </div>
    );
}

export default AskOut;
