import React, { useState } from 'react';

function AskOut() {
    const [position, setPosition] = useState({ top: 0, left: 0, init: true });

    // Function to handle click on the "No" button and move it randomly
    const handleNoClick = () => {
        if (position.init) {
            const newPosition = {
                top: Math.random() * (window.innerHeight - 100), // Avoiding edge overflow
                left: Math.random() * (window.innerWidth - 100),  // Avoiding edge overflow
                init: false
            };
            setPosition(newPosition);
        }
    };

    return (
        <div style={styles.container}>
            <p style={styles.text}>Do you wanna go out with me?<br/>Are you free tomorrow?</p>
            <div style={styles.buttonContainer}>
                <button style={styles.yesButton} onClick={() => alert('Yes! :)')}>Yes</button>
                <button 
                    style={{
                        ...styles.noButton, 
                        top: position.init ? 0 : `${position.top}px`,
                        left: position.init ? '10px' : `${position.left}px`,
                        position: position.init ? 'relative' : 'absolute'
                    }} 
                    onClick={handleNoClick}
                >
                    No
                </button>
            </div>
        </div>
    );
}

export default AskOut;

// Define styles in your component for simplicity and organization
const styles = {
    container: {
        textAlign: 'center',
        backgroundColor: '#b8e994',
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center'
    },
    text: {
        color: '#4a4a4a',
        fontFamily: '"Comic Sans MS", cursive, sans-serif',
        fontSize: '24px',
        marginBottom: '20px'
    },
    buttonContainer: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
    },
    yesButton: {
        margin: '10px',
        cursor: 'pointer',
        padding: '10px 20px',
        border: 'none',
        background: '#4CAF50', // Green button for positive action
        color: 'white',
        fontSize: '16px',
        borderRadius: '5px'
    },
    noButton: {
        margin: '10px',
        cursor: 'pointer',
        padding: '10px 20px',
        border: 'none',
        background: '#f44336', // Red button for negative action
        color: 'white',
        fontSize: '16px',
        borderRadius: '5px',
        transition: 'top 0.5s, left 0.5s' // Smooth transition for moving button
    }
};
